// src/components/TilePicker.js
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Tile from './Tile';

const PickerContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 20px;
  width: 130%;
  height: 190%;
`;

const possibleTiles = [
  [1, 2, 3], [1, 5, 9], [1, 6, 8], [2, 5, 7], [2, 6, 9],
  [3, 5, 8], [3, 6, 7], [4, 5, 6], [4, 7, 9], [5, 7, 8]
];

function TilePicker() {
  const [currentTile, setCurrentTile] = useState(null);

  useEffect(() => {
    pickRandomTile();
  }, []);

  const pickRandomTile = () => {
    const randomIndex = Math.floor(Math.random() * possibleTiles.length);
    setCurrentTile(possibleTiles[randomIndex]);
  };

  return (
    <PickerContainer>
      <div style={{ width: 104, height: 80 }}>
        {currentTile && <Tile values={currentTile} onClick={pickRandomTile} />}
      </div>
    </PickerContainer>
  );
}

export default TilePicker;
