import React from 'react';
import styled from 'styled-components';

const colors = {
  1: '#ff5733',  // Red
  2: '#33ff57',  // Green
  3: '#3357ff',  // Blue
  4: '#ff33a8',  // Pink
  5: '#33fff7',  // Cyan
  6: '#ffbd33',  // Orange
  7: '#bd33ff',  // Purple
  8: '#ff3333',  // Dark Red
  9: '#33ffbd'   // Mint
};

const TileContainer = styled.div`
  width: 100%;
  height: 100%;
  background-color: #ADD8E6;
  clip-path: polygon(25% 0%, 75% 0%, 100% 50%, 75% 100%, 25% 100%, 0% 50%);
  display: grid;
  grid-template-areas:
    "top top"
    "left right"
    "bottom bottom";
  color: white;
  font-weight: bold;
  font-size: 22px;
  cursor: pointer;
`;

const NumberTop = styled.div`
  grid-area: top;
  justify-self: center;
  align-self: start;
  mergin-top: 10px;
  background-color: ${props => colors[props.value]};
  padding: 5px 5px;
`;

const NumberLeft = styled.div`
  grid-area: left;
  justify-self: start;
  align-self: center;
  margin-left: 25px;
  background-color: ${props => colors[props.value]};
  padding: 5px 5px;
`;

const NumberRight = styled.div`
  grid-area: right;
  justify-self: end;
  align-self: center;
  margin-right: 25px;
  background-color: ${props => colors[props.value]};
  padding: 5px 5px;
`;

function Tile({ index, values, onClick }) {

  const onClickTile = () => {
    onClick(index - 1);
  }
  return (
    <TileContainer onClick={onClickTile}>
      { values &&
      <>
      <NumberTop value={values[0]}>{values[0]}</NumberTop>
      <NumberLeft value={values[1]}>{values[1]}</NumberLeft>
      <NumberRight value={values[2]}>{values[2]}</NumberRight>
      </>
      }
    </TileContainer>
  );
}

export default Tile;
